from flask import Flask, jsonify, make_responsefrom flask_httpauth import HTTPBasicAuthapp = Flask(__name__)auth = HTTPBasicAuth()users = {    "diachenko": "1",    "user1": "2"}@auth.get_passworddef get_pw(username):    if username in users:        return users.get(username)    return None@auth.error_handlerdef unauthorized():    return make_response(jsonify({'error': 'Unauthorized access'}), 401)@app.route('/')@auth.login_requireddef index():    return "Hello, %s!" % auth.username()if __name__ == '__main__':    app.run()